import React, { useState, useEffect } from 'react';
import axios from 'axios';

function StudentsPoints() {
    const [students, setStudents] = useState([]);
    const [newStudent, setNewStudent] = useState({
        name: '',
        tasks: 0,
        exam: 0,
        project: 0,
        attendance: 0
    });

    useEffect(() => {
        axios.get('/api/students')
            .then(response => setStudents(response.data))
            .catch(error => console.log(error));
    }, []);

    const handleChange = (event) => {
        const { name, value } = event.target;
        setNewStudent(prevState => ({ ...prevState, [name]: value }));
    }

    const handleSubmit = (event) => {
        event.preventDefault();
        axios.post('/api/students', newStudent)
            .then(response => {
                setStudents(prevState => [...prevState, response.data]);
                setNewStudent({
                    name: '',
                    tasks: 0,
                    exam: 0,
                    project: 0,
                    attendance: 0
                });
            })
            .catch(error => console.log(error));
    }

    const handleDelete = (id) => {
        axios.delete(`/api/students/${id}`)
            .then(response => setStudents(prevState => prevState.filter(student => student.id !== id)))
            .catch(error => console.log(error));
    }

    const handleUpdate = (id) => {
        // code to handle updating a student's information
    }

    const handleInputChange = (event, id) => {
        const { name, value } = event.target;
        // ensure input value is between 0 and 100
        const intValue = Math.max(0, Math.min(100, parseInt(value)));
        axios.put(`/api/students/${id}`, { [name]: intValue })
            .then(response => setStudents(prevState => prevState.map(student => {
                if (student.id === id) {
                    return { ...student, [name]: intValue };
                }
                return student;
            })))
            .catch(error => console.log(error));
    }

    return (
        <div className="students-points">
            <h1>Record of Students' Points</h1>
            <form onSubmit={handleSubmit}>
                <label>
                    Name:
                    <input type="text" name="name" value={newStudent.name} onChange={handleChange} />
                </label>
                <label>
                    Tasks:
                    <input type="number" name="tasks" value={newStudent.tasks} min="0" max="100" onChange={handleChange} />
                </label>
                <label>
                    Exam:
                    <input type="number" name="exam" value={newStudent.exam} min="0" max="100" onChange={handleChange} />
                </label>
                <label>
                    Project:
                    <input type="number" name="project" value={newStudent.project} min="0" max="100" onChange={handleChange} />
                </label>
                <label>
                    Attendance:
                    <input type="number" name="attendance" value={newStudent.attendance} min="0" max="100" onChange={handleChange} />
                </label>
                <button type="submit">Add Student</button>
            </form>
            <table>
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Tasks</th>
                        <th>Exam</th>
                        <th>Project</th>
                        <th>Attendance</th>
                        <th>Points</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {students.map(student => (
                        <tr key={student.id}>
                            <td>{student.name}</td>
                            <td>
                                <input type="number" name="tasks" value={student.tasks} min="0" max="100" onChange={(event) => handleInputChange(event, student.id)} />
                            </td>
                            <td>
                                <input type="number" name="exam" value={student.exam} min="0" max="100" onChange={(event) => handleInputChange(event, student.id)} />
                            </td>
                            <td>
                                <input type="number" name="project" value={student.project} min="0" max="100" onChange={(event) => handleInputChange(event, student.id)} />
                            </td>
                            <td>
                                <input type="number" name="attendance" value={student.attendance} min="0" max="100" onChange={(event) => handleInputChange(event, student.id)} />
                            </td>
                            <td>{((student.tasks + student.exam + student.project + student.attendance) / 4).toFixed(2)}</td>
                            <td>
                                <button onClick={() => handleUpdate(student.id)}>Update</button>
                                <button onClick={() => handleDelete(student.id)}>Delete</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
}

export default StudentsPoints;
