import React, { useState, useEffect } from 'react';
import axios from 'axios';

function StudentsPoints() {
    const [students, setStudents] = useState([]);
    const [newStudent, setNewStudent] = useState({
        name: '',
        tasks: 0,
        exam: 0,
        project: 0,
        attendance: 0
    });

    useEffect(() => {
        axios.get('/api/students')
            .then(response => setStudents(response.data))
            .catch(error => console.log(error));
    }, []);

    const handleInputChange = (event, id) => {
        const { name, value } = event.target;
        setStudents(prevState => prevState.map(student => {
            if (student.id === id) {
                return { ...student, [name]: Number(value) };
            } else {
                return student;
            }
        }));
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        axios.post('/api/students', newStudent)
            .then(response => {
                setStudents(prevState => [...prevState, response.data]);
                setNewStudent({
                    name: '',
                    tasks: 0,
                    exam: 0,
                    project: 0,
                    attendance: 0
                });
            })
            .catch(error => console.log(error));
    };

    const handleDelete = (id) => {
        axios.delete(`/api/students/${id}`)
            .then(response => {
                setStudents(prevState => prevState.filter(student => student.id !== id));
            })
            .catch(error => console.log(error));
    };

    const handleUpdate = (id) => {
        const studentToUpdate = students.find(student => student.id === id);
        axios.put(`/api/students/${id}`, studentToUpdate)
            .then(response => console.log(response))
            .catch(error => console.log(error));
    };

    return (
        <div className="students-points" style={{ backgroundColor: 'blue' }}>
            <h1>Record of Students' Points</h1>
            <form onSubmit={handleSubmit}>
                <label htmlFor="name">Name:</label>
                <input type="text" name="name" value={newStudent.name} onChange={(event) => setNewStudent(prevState => ({ ...prevState, name: event.target.value }))} required />
                <label htmlFor="tasks">Tasks:</label>
                <input type="number" name="tasks" value={newStudent.tasks} min="0" max="100" onChange={(event) => setNewStudent(prevState => ({ ...prevState, tasks: Number(event.target.value) }))} required />
                <label htmlFor="exam">Exam:</label>
                <input type="number" name="exam" value={newStudent.exam} min="0" max="100" onChange={(event) => setNewStudent(prevState => ({ ...prevState, exam: Number(event.target.value) }))} required />
                <label htmlFor="project">Project:</label>
                <input type="number" name="project" value={newStudent.project} min="0" max="100" onChange={(event) => setNewStudent(prevState => ({ ...prevState, project: Number(event.target.value) }))} required />
                <label htmlFor="attendance">Attendance:</label>
                <input type="number" name="attendance" value={newStudent.attendance} min="0" max="100" onChange={(event) => setNewStudent(prevState => ({ ...prevState, attendance: Number(event.target.value) }))} required />
                <button type="submit">Add Student</button>
            </form>
            <table style={{ backgroundColor: 'white' }}>
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Tasks</th>
                        <th>Exam</th>
                        <th>Project</th>
                        <th>Attendance</th>
                        <th>Points</th>
                        <th></th>
                        <th></th>
                    </tr>
                </thead>
                <tbody>
                    {students.map(student => (
                        <tr key={student.id}>
                            <td>{student.name}</td>
                            <td><input type="number" name="tasks" value={student.tasks} min="0" max="100" onChange={(event) => handleInputChange(event, student.id)} /></td>
                            <td><input type="number" name="exam" value={student.exam} min="0" max="100" onChange={(event) => handleInputChange(event, student.id)} /></td>
                            <td><input type="number" name="project" value={student.project} min="0" max="100" onChange={(event) => handleInputChange(event, student.id)} /></td>
                            <td><input type="number" name="attendance" value={student.attendance} min="0" max="100" onChange={(event) => handleInputChange(event, student.id)} /></td>
                            <td>{((student.tasks + student.exam + student.project + student.attendance) / 4).toFixed(2)}</td>
                            <td><button onClick={() => handleUpdate(student.id)}>Update</button></td>
                            <td><button onClick={() => handleDelete(student.id)}>Delete</button></td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
}

export default StudentsPoints;
