import React, { useState } from 'react';
import './App.css';

function App() {
    const [students, setStudents] = useState([]);
    const [showFinalTable, setShowFinalTable] = useState(false);

    const handleAddStudent = () => {
        const isEditing = students.some((student) => student.editing);
        if (isEditing) {
            alert('Please finish editing the current student before adding a new one.');
            return;
        }
        const newStudent = { name: '', exam: 0, project: 0, tasks: 0, attendance: 0, editing: true };
        setStudents([...students, newStudent]);
    };

    const handleDeleteStudent = (index) => {
        const newStudents = [...students];
        newStudents.splice(index, 1);
        setStudents(newStudents);
    };

    const handleEditStudent = (index) => {
        const newStudents = [...students];
        newStudents[index].editing = true;
        setStudents(newStudents);
    };

    const handleSaveStudent = (index) => {
        const newStudents = [...students];
        if (newStudents[index].name.trim() === '') {
            alert('Please enter a first and last name for the student.');
            return;
        }
        newStudents[index].editing = false;

        const average = calculateAverage(newStudents[index]);
        const finalGrade = getFinalGrade(average);
        const cellStyle = getCellStyle(average);

        newStudents[index].average = average;
        newStudents[index].finalGrade = finalGrade;
        newStudents[index].cellStyle = cellStyle;

        setStudents(newStudents);
    };

    const handleCancelEdit = (index) => {
        const newStudents = [...students];
        newStudents[index].editing = false;
        setStudents(newStudents);
    };

    const handleNameChange = (index, event) => {
        const newStudents = [...students];
        newStudents[index].name = event.target.value;
        setStudents(newStudents);
    };

    const handlePointsChange = (category, index, event) => {
        const newStudents = [...students];
        newStudents[index][category] = Math.min(100, Number(event.target.value));
        setStudents(newStudents);
    };

    const calculateAverage = (student) => {
        const totalPoints = student.exam + student.project + student.tasks + student.attendance;
        return (totalPoints / 4).toFixed(2);
    };

    const getFinalGrade = (average) => {
        if (average < 50) return 'Fail';
        if (average < 62.5) return '2';
        if (average < 75) return '3';
        if (average < 87.5) return '4';
        return '5';
    };

    const getCellStyle = (average) => {
        return average < 50 ? { backgroundColor: 'red', color: 'white' } : { backgroundColor: 'green', color: 'white' };
    };

    const handleBuildTable = () => {
        setShowFinalTable(true);
    };

    const finalTable = students.map((student, index) => {
        const { name, exam, project, tasks, attendance, average, finalGrade, cellStyle } = student;
        return (
            <tr key={index}>
                <td>{name}</td>
                <td>{exam}</td>
                <td>{project}</td>
                <td>{tasks}</td>
                <td>{attendance}</td>
                <td>{average}</td>
                <td style={cellStyle}>{finalGrade}</td>
            </tr>
        );
    });

    return (
        <div>
            <h1>Evidencija bodova studenata</h1>
            <button onClick={handleAddStudent}>ADD STUDENT</button>
            <button onClick={handleBuildTable}>BUILD FINAL TABLE</button>
            <table>
                <thead>
                    <tr>
                        <th>NAME</th>
                        <th>EXAM</th>
                        <th>PROJECT</th>
                        <th>TASKS</th>
                        <th>ATTENDANCE</th>
                        <th>POINTS</th>
                        <th>FINAL GRADE</th>
                        <th></th>
                    </tr>
                </thead>
                <tbody>
                    {students.map((student, index) => {
                        const average = calculateAverage(student);
                        const finalGrade = getFinalGrade(average);
                        const cellStyle = getCellStyle(average);

                        return (
                            <tr key={index}>
                                <td>
                                    {student.editing ? (
                                        <input type="text" value={student.name} onChange={(event) => handleNameChange(index, event)} />
                                    ) : (
                                        <span>{student.name}</span>
                                    )}
                                </td>
                                <td>
                                    {student.editing ? (
                                        <input type="number" max="100" value={student.exam} onChange={(event) => handlePointsChange('exam', index, event)} />
                                    ) : (
                                        <span>{student.exam}</span>
                                    )}
                                </td>
                                <td>
                                    {student.editing ? (
                                        <input type="number" max="100" value={student.project} onChange={(event) => handlePointsChange('project', index, event)} />
                                    ) : (
                                        <span>{student.project}</span>
                                    )}
                                </td>
                                <td>
                                    {student.editing ? (
                                        <input type="number" max="100" value={student.tasks} onChange={(event) => handlePointsChange('tasks', index, event)} />
                                    ) : (
                                        <span>{student.tasks}</span>
                                    )}
                                </td>
                                <td>
                                    {student.editing ? (
                                        <input type="number" max="100" value={student.attendance} onChange={(event) => handlePointsChange('attendance', index, event)} />
                                    ) : (
                                        <span>{student.attendance}</span>
                                    )}
                                </td>
                                <td>{average}</td>
                                <td style={cellStyle}>
                                    {finalGrade}
                                </td>
                                <td>
                                    {student.editing ? (
                                        <>
                                            <button onClick={() => handleSaveStudent(index)}>SAVE</button>
                                            <button onClick={() => handleCancelEdit(index)}>CANCEL</button>
                                        </>
                                    ) : (
                                        <button onClick={() => handleEditStudent(index)}>EDIT</button>
                                    )}
                                    <button onClick={() => handleDeleteStudent(index)}>DELETE</button>
                                </td>
                            </tr>
                );
                    })}
            </tbody>
        </table>
            {
        showFinalTable && (
            <div>
                <h1>Final Build Table</h1>
                <table>
                    <thead>
                        <tr>
                            <th>NAME</th>
                            <th>EXAM</th>
                            <th>PROJECT</th>
                            <th>TASKS</th>
                            <th>ATTENDANCE</th>
                            <th>POINTS</th>
                            <th>FINAL GRADE</th>
                        </tr>
                    </thead>
                    <tbody>{finalTable}</tbody>
                </table>
            </div>
        )
    }
        </div >
    );
}

export default App;
