import React, { useState } from 'react';
import './App.css';

function App() {
    const [students, setStudents] = useState([]);

    const handleAddStudent = () => {
        const newStudent = { name: '', exam: 0, project: 0, tasks: 0, attendance: 0 };
        setStudents([...students, newStudent]);
    };

    const handleDeleteStudent = (index) => {
        const newStudents = [...students];
        newStudents.splice(index, 1);
        setStudents(newStudents);
    };

    const handleEditStudent = (index) => {
        const newStudents = [...students];
        newStudents[index].editing = true;
        setStudents(newStudents);
    };

    const handleSaveStudent = (index) => {
        const newStudents = [...students];
        newStudents[index].editing = false;
        setStudents(newStudents);
    };

    const handleCancelEdit = (index) => {
        const newStudents = [...students];
        newStudents[index].editing = false;
        setStudents(newStudents);
    };

    const handleNameChange = (index, event) => {
        const newStudents = [...students];
        newStudents[index].name = event.target.value;
        setStudents(newStudents);
    };

    const handlePointsChange = (category, index, event) => {
        const newStudents = [...students];
        newStudents[index][category] = Math.min(100, Number(event.target.value));
        setStudents(newStudents);
    };

    const calculateAverage = (student) => {
        const totalPoints = student.exam + student.project + student.tasks + student.attendance;
        return (totalPoints / 4).toFixed(2);
    };

    return (
        <div>
            <h1>Evidencija bodova studenata</h1>
            <button onClick={handleAddStudent}>ADD STUDENT</button>
            <table>
                <thead>
                    <tr>
                        <th>NAME</th>
                        <th>EXAM</th>
                        <th>PROJECT</th>
                        <th>TASKS</th>
                        <th>ATTENDANCE</th>
                        <th>POINTS</th>
                        <th></th>
                    </tr>
                </thead>
                <tbody>
                    {students.map((student, index) => (
                        <tr key={index}>
                            <td>
                                {student.editing ? (
                                    <input type="text" value={student.name} onChange={(event) => handleNameChange(index, event)} />
                                ) : (
                                    <span>{student.name}</span>
                                )}
                            </td>
                            <td>
                                {student.editing ? (
                                    <input type="number" max="100" value={student.exam} onChange={(event) => handlePointsChange('exam', index, event)} />
                                ) : (
                                    <span>{student.exam}</span>
                                )}
                            </td>
                            <td>
                                {student.editing ? (
                                    <React.Fragment>
                                        <input type="number" max="100" value={student.tasks} onChange={(event) => handlePointsChange('tasks', index, event)} />
                                        <span>{student.tasks}</span>
                                    </React.Fragment>
                                ) : (
                                    <span>{student.tasks}</span>
                                )}
                            </td>
                            <td>
                                {student.editing ? (
                                    <>
                                        <input type="number" max="100" value={student.tasks} onChange={(event) => handlePointsChange('tasks', index, event)} />
                                        <span>{student.tasks}</span>
                                    </>
                                ) : (
                                    <span>{student.tasks}</span>
                                )}
                            </td>
                            <td>
                                {student.editing ? (
                                    <input type="number" max="100" value={student.attendance} onChange={(event) => handlePointsChange('attendance', index, event)} />
                                ) : (
                                    <span>{student.attendance}</span>
                                )}
                            </td>
                            <td>{calculateAverage(student)}</td>
                            <td>
                                {student.editing ? (
                                    <>
                                        <button onClick={() => handleSaveStudent(index)}>SAVE</button>
                                        <button onClick={() => handleCancelEdit(index)}>CANCEL</button>
                                    </>
                                ) : (
                                    <button onClick={() => handleEditStudent(index)}>EDIT</button>
                                )}
                                <button onClick={() => handleDeleteStudent(index)}>DELETE</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
}

export default App;
